import React, { Component } from 'react'
import axios from 'axios';
import { Link } from 'react-router-dom';
import ParallaxBlog from './ParallaxBlog';
import ErrorBoundary from './ErrorBoundary';

class BlogPosts extends Component {
    constructor(props) {
        super(props)
        this.state = {
            posts: []
        }
    }

    componentDidMount() {
        axios.get('http://mohammadmohsin.altervista.org/blog/posts.php')
        //axios.get('http://mohammadmohsin.altervista.org/blog/wp-json/wp/v2/posts')
        .then(response => {
            console.log(response)
            this.setState({posts: response.data})
        })
        .catch(error=> {
            console.log(error)
        })
    }

    render() {
        const { posts } = this.state
        return (
            <>
            <ErrorBoundary>
                <ParallaxBlog />
            </ErrorBoundary>
            <div className='w3-content w3-container w3-padding-64'>
              <div className='w3-row'>
                <div className='w3-ul w3-large'>
                {
                    posts.length ?
                    posts.map(post => 
                        <div class="w3-card-4" style={{marginBottom: '64px'}}>

                            <header class="w3-container w3-light-grey">
                                <h3>{post.post_title}</h3>
                            </header>

                            <div className='w3-container'>
                                <p>{post.auth_first_name} {post.auth_last_name}</p>
                                <hr/>
                            <img src={post.img_url} alt="Avatar" className='w3-left w3-circle' style={{width: '100%', borderRedius: '0'}} />
                                <p>President/CEO at Mighty Schools...</p>
                            </div>

                            <button className='w3-button w3-block w3-dark-grey'>+ Connect</button>

                        </div>
                    ) :
                    //<li className='w3-border-0' key={post.post_id} ><a href={ post.post_url } target="_blank">{post.post_title} <img src={post.img_url} />
                    //posts.map(post => <li key={post.id}><a href={ post.guid.rendered } target="_blank">{post.id} {post.title.rendered} </a></li>) :
                    null
                }
                </div>
              </div>
            </div>
            </>
        );
    }
}

export default BlogPosts
