import React, { Component } from 'react'
import axios from 'axios';
import { Link } from 'react-router-dom';

class BlogPosts extends Component {
    constructor(props) {
        super(props)
        this.state = {
            posts: []
        }
    }

    componentDidMount() {
        //axios.get('http://mohammadmohsin.altervista.org/blog/posts.php')
        axios.get('http://mohammadmohsin.altervista.org/blog/wp-json/wp/v2/posts')
        .then(response => {
            console.log(response)
            this.setState({posts: response.data})
        })
        .catch(error=> {
            console.log(error)
        })
    }

    render() {
        const { posts } = this.state
        return (
            <ul>
                {
                    //posts.map(post => <li key={post.id}><a href={ post.guid } target="_blank">{post.id} {post.title} </a></li>) :
                    posts.length ?
                    posts.map(post => <li key={post.id}>{post.id} {post.title.rendered} </li>) :
                    null
                }
            </ul>
        );
    }
}

export default BlogPosts
